mapper.select:
select * from mwsense where sense_category=(
		select sense_detail_category from mwsense_category where num=#{num} 
	)
order by num desc

mapper.update:
update mwsense
num=#{num}, main_set=#{main_set}, sense_category=#{sense_category}, sense_title=#{sense_title}, sense_content=#{sense_content}, sense_url=#{sense_url}, sense_thumbnail=#{sense_thumbnail}
where num=#{num}
